function setCookie(name,value,expireHour){//濠电村堕稿宕剧婵濠妤р婵奸モ濡渚娈缂浣规ㄩ°х婢舵垫告侗╅々濉okie浣圭归宀??拷椤绠哄╁撮风芥崇锟筋婚藉骞跺┑村堕尖绂濠靛锝骞娴ｅワ璺ㄥ凤??var exp  = new Date();    //new Date("December 31, 9998");
	exp.setTime(exp.getTime() + expireHour*60*60*1000);
	document.cookie = name + "="+ escape (value) + ";path=/;expires=" + exp.toGMTString();
}

